package com.ubtechinc.alpha.utils;import com.ubtech.utilcode.utils.LogUtils;import com.ubtechinc.alpha.robotinfo.Alpha2Version;/** * @author wzt * @date 2017/5/23 * @Description 获取软件版本的工具类 * @modifier * @modify_time */public class SoftwareVersionUtils {    public static Alpha2Version parseVersion(byte[] bytes, int len) {        if (len < 3) {            LogUtils.e("firm Version parse error!");            return null;        }        Alpha2Version mVersion = new Alpha2Version();        byte[] versionData = new byte[len - 2];        System.arraycopy(bytes, 2, versionData, 0, len - 2);        String version = new String(versionData);        mVersion.setVersion(version);        return mVersion;    }    public static Alpha2Version parseBatteryVersion(byte[] bytes, int len) {        if (len < 4) {            LogUtils.e("Battery Version parse error!");            return null;        }        Alpha2Version mVersion = new Alpha2Version();        if(bytes[1] == 3) {            byte[] versionData = new byte[len - 2];            System.arraycopy(bytes, 2, versionData, 0, len - 2);            String version = new String(versionData);            mVersion.setVersion(version);        }        return mVersion;    }}